CREATE DATABASE  IF NOT EXISTS `sr`;
USE `sr`;

DROP TABLE IF EXISTS `site`;
CREATE TABLE `site` (
  `site_id` int(11) NOT NULL AUTO_INCREMENT,
  `site_name` varchar(60) NOT NULL,
  `locale` varchar(20) NOT NULL,
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `status` varchar(10) NOT NULL,
  PRIMARY KEY (`site_id`),
  UNIQUE KEY `site_name_UNIQUE` (`site_name`)
);

INSERT INTO `site` VALUES (1,'English','english','2018-04-06 00:00:00','active'),(2,'French','french','2018-04-17 11:41:45','active');

DROP TABLE IF EXISTS `page`;
CREATE TABLE `page` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pageName` varchar(45) NOT NULL,
  `page_title` varchar(50) DEFAULT NULL,
  `title` varchar(50) DEFAULT NULL,
  `heading1` varchar(50) DEFAULT NULL,
  `heading2_black` varchar(50) DEFAULT NULL,
  `heading2_white` varchar(50) DEFAULT NULL,
  `heading3` text,
  `site_id` int(11) NOT NULL,
  `status` varchar(15) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11;

INSERT INTO `page` VALUES (1,'home','Spot the risk','Spot the risk','Heading 1','Heading 2','','',1,'active'),(2,'quiz','Spot the risk','Play Spot the Risk','Welcome to...','SPOT THE RISK','TRAVEL CHALLENGE','We think we all know the basics of travel safety. But what about the risks you don\'t think about? The little events that can quickly turn into a big problem.',1,'active'),(3,'app','Get the App','Get the App','Get the App','INTERNATIONAL SOS ASSISTANCE APP','','The Assistance App',1,'active'),(4,'about-us','About Us','About International SOS','About','INTERNATIONAL SOS',NULL,'Every day millions of International SOS members put their trust in us to provide solutions that help them wherever they live or travel, 24 hours a day, 365 days a year.',1,'active'),(5,'leaderboard','Leaderboard','Leaderboard','Spot the Risk','LEADERBOARD',NULL,'',1,'active'),(6,'home','Repérer le risque',NULL,NULL,NULL,NULL,NULL,2,'active'),(7,'quiz','Repérer le risque','Jouer repérer le risque','Bienvenue à...','SPOT LE RISQUE','DÉFI DE VOYAGE','Nous pensons que nous connaissons tous les bases de la sécurité des déplacements. Mais qu\\\'en est-il des risques auxquels vous ne pensez pas? Les petits événements qui peuvent rapidement se transformer en un gros problème.',2,'active'),(8,'app','Obtenir l\'application','Obtenir l\'application','Obtenir l\'application','APP SOS ASSISTANCE INTERNATIONALE',NULL,'L\'application d\'assistance',2,'active'),(9,'about-us','À propos de nous','A propos de International SOS','A propos de','INTERNATIONAL SOS',NULL,'Chaque jour, des millions de membres SOS internationaux nous font confiance pour proposer des solutions qui les aident partout où ils vivent ou voyagent, 24 heures sur journée, 365 jours par an.',2,'active'),(10,'leaderboard','Classement','Classement','Repérer le risque','CLASSEMENT',NULL,NULL,2,'active');

DROP TABLE IF EXISTS `info`;
CREATE TABLE `info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `text_id` int(11) DEFAULT NULL,
  `text` text,
  `page_id` int(11) DEFAULT NULL,
  `site_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18;

INSERT INTO `info` VALUES (1,1,'',1,1),(2,1,'Play \'Spot the Risk!\' to see if you travel as safely as you thought. Take the quiz and submit your score for a chance to win. The top scorers will be entered into a random prize draw for a gift card from Amazon or Target worth 200 USD.',2,1),(3,2,'Learn some astonishing facts along the way and see how the International SOS App could help you in your travels.',2,1),(4,1,'When you are on the move the App is an essential tool to help you make the most of your International SOS membership.',3,1),(5,2,'By providing unprecedented access to International SOS logistics, medical and security advice and capabilities you can get essential travel help when you need it.',3,1),(6,1,'Through our worldwide network of assistance centres, clinics, health and logistics providers, we offer local expertise, preventative advice and emergency assistance during critical illness, accident, or civil unrest. With over 10,000 employees working in 70 countries we work in partnership with businesses, governments and NGOs to offer international standards of medical care.',4,1),(7,1,'Knowledgeable of food and water safety (piece of cake), experts at packing and always wearing their seat belts, see the best Riskspotters below.',5,1),(8,1,'',1,2),(9,1,'Jouer \'repérer le risque!\' pour voir si vous voyagez aussi prudemment que vous le pensiez. Répondez au quiz et soumettez votre score pour avoir une chance de gagner. Les meilleurs buteurs seront inscrits à un tirage au sort aléatoire pour une carte-cadeau d\'Amazon ou Target d\'une valeur de 200 USD.',2,2),(10,2,'Apprenez quelques faits étonnants en cours de route et découvrez comment l\'application internationale SOS pourrait vous aider dans vos voyages.',2,2),(11,1,'Lorsque vous êtes en déplacement l\'App est un outil essentiel pour vous aider à tirer le meilleur parti de votre adhésion International SOS.',3,2),(12,2,'En offrant un accès sans précédent à la logistique internationale SOS, à des conseils et des capacités médicales et de sécurité, vous pouvez obtenir une aide de voyage essentielle lorsque vous en avez besoin.',3,2),(13,1,'Grâce à notre réseau mondial de centres d\'assistance, de cliniques, de fournisseurs de soins de santé et de logistique, nous offrons une expertise locale, des conseils préventifs et une aide d\'urgence en cas de maladie grave, d\'accident ou de troubles civils. Avec plus de 10 000 employés travaillant dans 70 pays, nous travaillons en partenariat avec des entreprises, des gouvernements et des ONG pour offrir des normes internationales de soins médicaux.',4,2),(14,1,'Connaissant la sécurité des aliments et de l\'eau (morceau de gâteau), les experts de l\'emballage et toujours porter leur ceinture de sécurité, voir les meilleurs Riskspotters ci-dessous.',5,2);

