delimiter //
create procedure `load_scores`(in user_id int)
begin
(select name,score, find_in_set( score,( select group_concat(score order by score desc)from leaderboard ) )  as rankof from leaderboard order by rankof limit 5) union
(select name,score, find_in_set( score,( select group_concat(score order by score desc)from leaderboard ) )  as rankof from leaderboard where id=user_id order by rankof) ;
end //

delimiter //
create procedure load_leaderboard ()
begin
select name,score, find_in_set( score,( select group_concat(score order by score desc)from leaderboard ) )  as rankof from leaderboard order by rankof limit 10;
end //
